tinymce.PluginManager.add('replaceclass', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('replaceclass', {
        text: 'Полная очистка',
        icon: false,
        onclick: function() {
				editor.setContent(editor.getContent().replace(/<A.+?href\s*=.*?>(.*?)<\s*\/A\s*>/gim, '$1'));
   				editor.setContent(editor.getContent().replace(/<div[\s\S]*?>([\s\S]*?)<\s*\/div\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<span[\s\S]*?>([\s\S]*?)<\s*\/span\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<strong.*?>([\s\S]*?)<\s*\/strong\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<em[\s\S]*?>([\s\S]*?)<\s*\/em\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<h1[\s\S]*?>([\s\S]*?)<\s*\/h1\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<h2[\s\S]*?>([\s\S]*?)<\s*\/h2\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<h3[\s\S]*?>([\s\S]*?)<\s*\/h3\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<h4[\s\S]*?>([\s\S]*?)<\s*\/h4\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<blockquote[\s\S]*?>([\s\S]*?)<\s*\/blockquote\s*>/gim, '$1'));
				editor.setContent(editor.getContent().replace(/<br[\s\S]*?>/gim, '<br />'));				
				editor.setContent(editor.getContent().replace(/<p[\s\S]*?>/gim, '<p>'));				
				editor.setContent(editor.getContent().replace(/<span[\s\S]*?>/gim, '<span>'));
        }
    });

});
